mapper.select:
SELECT cp.NUM_ID numId, cp.VC_HIGHT vcHight, cp.VC_WIGHT vcWight, cp.VC_NAME vcName, cp.VC_PHONE vcPhone, cp.NUM_TYPE numType, cp.NUM_NUM numNum, cp.NUM_STATE numState, cp.DAT_CREAT_TIME datCreatTime, cp.DAT_UPDATE_TIME datUpdateTime, cp.NUM_PRICE numPrice, cp.VC_USER_ID vcUserId, cp.VC_OPEN_ID vcOpenId, cp.DAT_CONFIRM_TIME datConfirmTime, cp.VC_NOTES vcNotes, cp.NUM_SCHOOL_ID numSchoolId, cp.NUM_GRADE_ID numGradeId, cp.NUM_MODEL_ID numModelId, cp.VC_ORDER_NO vcOrderNo, cp.VC_UNION_ID vcUnionId, cp.VC_SIGN_DESC vcSignDesc, cp.NUM_PAR_TYPE numParType, cp.NUM_PAY_STATE numPayState, cp.VC_EXPIRE_TIME vcExpireTime, cfg.VC_GRADE_NAME vcGradeName, cfs.VC_SCHOOL_NAME vcSchoolName, cfm.VC_MODEL_NAME vcModelName FROM CL_PRODUCT cp LEFT JOIN CL_FN_GRADE cfg ON cp.NUM_GRADE_ID = cfg.NUM_ID LEFT JOIN CL_FN_SCHOOL cfs ON cp.NUM_SCHOOL_ID = cfs.NUM_ID LEFT JOIN CL_FN_MODEL cfm ON cp.NUM_MODEL_ID = cfm.NUM_ID WHERE ( cp.NUM_IS_DEL = 1
AND cp.VC_USER_ID = #{VC_USER_ID}
AND cp.VC_NAME LIKE CONCAT('%',#{VC_NAME},'%')
AND cp.VC_PHONE LIKE CONCAT('%',#{VC_PHONE},'%')
AND cp.NUM_TYPE = #{NUM_TYPE}
AND cp.NUM_STATE = #{NUM_STATE}
AND cp.NUM_PAY_STATE = #{NUM_PAY_STATE}
AND cp.NUM_PAR_TYPE = #{NUM_PAR_TYPE}
AND cp.NUM_ID = #{NUM_ID}
AND cp.NUM_SCHOOL_ID = #{NUM_SCHOOL_ID}
AND cp.NUM_GRADE_ID = #{NUM_GRADE_ID}
AND cp.NUM_MODEL_ID = #{NUM_MODEL_ID}
AND cp.VC_ORDER_NO LIKE CONCAT('%',#{VC_ORDER_NO},'%')
and DATE_FORMAT(cp.DAT_CREAT_TIME,'%Y-%m-%d') >= DATE_FORMAT(#{DAT_CREAT_TIME_START},'%Y-%m-%d')
and DATE_FORMAT(cp.DAT_CREAT_TIME,'%Y-%m-%d') <= DATE_FORMAT(#{DAT_CREAT_TIME_END},'%Y-%m-%d')
) ORDER BY cp.DAT_CREAT_TIME DESC
